import { buildSchema } from "graphql";

const schema = buildSchema(`
    type Product{
        id: ID
        name: String
        description: String
        price: Float
        soldout: Soldout
        inventory: Int
        stores: [Store]!
    }

    enum Soldout{
        SOLDOUT
        ONSALE
    }

    type Store{
        store: String
    }

    type Query{
        getProduct(id: ID): Product
        getAllProduct: [Product]
    }

    input StoreInput{
        store: String
    }

    input ProductInput{
        id: ID
        name: String
        description: String
        price: Float
        soldout: Soldout
        inventory: Int
        stores: [StoreInput]!
    }

    input UpdateProductInput {
        name: String
        description: String
        price: Float
        soldout: Soldout
        inventory: Int
        stores: [StoreInput]!
    }

    type Mutation{
        createProduct(input: ProductInput): Product
        updateProduct(id: ID, input: UpdateProductInput): Product
        deleteProduct(id: ID): Product
    }
`);
export default schema;

