        soldout: Boolean
        stores: [Store]!
    }

    type Store{
        store: String
    }

    type Query{
        product: Product
    }

    type StoreInput{
        store: String
    }

    input ProductInput{
        id: ID
        name: String
        description: String
        price: Float
        soldout: Boolean
        stores: [StoreInput]!
    }

    type Mutation{
        createProduct
    }
`);
export default schema;
